import styled from "styled-components";

export const CartIconContainer = styled.div`
   width: 25px;
   position: relative;
   cursor: pointer;
   display: flex;
   align-items: center;
   justify-content: center;
`;

export const CartIconNumber = styled.span`
   position: absolute;
   top: 9px;
   font-size: 12px;
`;
